import React, { useState, useContext } from 'react';
import { Container, Row, Col, Button, Jumbotron} from 'react-bootstrap';
import AppContext from './context/AppContext'   
import { useHistory } from "react-router-dom";

function Cart(props){
    const context = useContext(AppContext)
    let history = useHistory();
    const cart = context.cart
    const arr_cart = Object.entries(cart)
    const handleClick =(e) => {
        console.log(cart,'<---------') 
        if(cart !={})history.push("/PointOfSale");
    }

    return(
        <div>

            <Jumbotron className='text-center'>
                <h1>This is the cart!</h1>
                <div style={{backgroundColor:'white'}}>
                <Container>
                    <Row>
                        <Col><h4>Name</h4></Col><Col><h4>Quantity</h4></Col><Col><h4>Price</h4></Col><Col><h4>Remove Row</h4></Col>
                    </Row>
                </Container>
                
                    {arr_cart.map(item => {
                        let item_info = context.product.find(x=> x.id===parseInt(item[0]))
                        return(
                        <div key={item[0]+'ID'}>
                            <Container>
                                <Row>
                                    <Col><p>{item_info.name}</p></Col><Col> {item[1]}</Col><Col> ${item_info.price*item[1]}</Col><Col> <i className="fas fa-minus-circle" onClick={e=>{context.removeFromCart(item[0])}}> </i></Col>
                                </Row>
                            </Container>
                        </div>
                    )})}
                </div>
                <Button type="submit" className='button-spacing' variant="warning" onClick={e =>{
                    handleClick()}}
                >
                    Proceed to Check Out
                </Button>
    
            </Jumbotron>

            







        </div>
    )
}
export default Cart;
